import streamlit as st

st.title('안녕하세요~')

st.write('Hello world')

if st.button('노크하기') :
    st.write('여기 사람 있어요..~')

## Checkbox
st.write('동의하시면 아래 내용에 체크해주세요.')
agree = st.checkbox('동의합니다')
if agree :
    st.write('버튼눌림')


## Slidebox
age = st.slider('당신은 몇살인가요?', 0, 130, 25) # 시작값, 종료값, default값
st.write(f'저는 {age}살입니다.')

## Text
text1 = st.text_input('이름을 입력하세요.')
text2 = st.text_area('자기소개')
st.write(f'이름: {text1}')
st.write(f'자기소개: {text2}')

## Image
st.image('data:image/webp;base64,')

